(1, 577, 'Thành Phùng', 'thangnh@gmail.com', '/member/hinhanh1/1663298395.jpg', 'thành đang bình luận', 2, NULL, '2022-09-16 03:19:55', '2022-10-14 09:32:57', 1),
(2, 584, 'Hữu Thắng', 'huuthangb2k50@gmail.com', '/', 'Nhập bình luận', 1, NULL, '2022-09-28 08:48:37', '2022-09-28 08:48:37', 2),
(3, 605, 'Hữu Thắng', 'huuthangb2k50@gmail.com', '/', 'Thành đang bình luận bài viết', 1, NULL, '2022-09-28 08:50:40', '2022-09-28 08:50:40', 2),
