INSERT INTO `tb_post_history` (`id`, `action`, `post_id`, `taxonomy_id`, `title`, `brief`, `content`, `is_type`, `json_params`, `image`, `image_thumb`, `iorder`, `status`, `author`, `url_coppy`, `url_part`, `torder`, `number_comment`, `number_like`, `number_view`, `nhuanbut`, `aproved_date`, `rating`, `comment_count`, `category`, `cms_tag`, `news_position`, `relation`, `comment_status`, `file`, `user_aproved`, `seo_title`, `seo_keyword`, `seo_description`, `admin_created_id`, `admin_updated_id`, `created_at`, `updated_at`) VALUES
(76, 'edit', 627, 42, 'Khoa Binh chủng chuẩn bị bài giảng cho lớp tập huấn của Sư đoàn 10, Quân đoàn 3', 'Thực hiện theo lộ trình hiện đại hóa quân đội theo hướng tinh, gọn. Bộ trưởng Bộ Quốc phòng đã quyết định thành lập thí điểm Sự đoàn 10, Quân đoàn 3 với nhiều vũ khi, trang bị hiện đại lần đầu được đưa vào biên chế cho sư đoàn bộ binh.', '<p style=\"text-align:justify;\">Dự án đường sắt đô thị Nhổn - ga Hà Nội dự kiến đưa vào khai thác 8,5km đoạn trên cao vào cuối năm 2021, song hiện phải lùi đến cuối năm sau. Trong khi đó, đến nay vẫn có gói thầu còn tới 30 - 50% khối lượng chưa hoàn thành.</p><p style=\"text-align:justify;\"><strong>Vừa thử nghiệm vừa thi công Depot</strong></p><p style=\"text-align:justify;\">Những ngày gần đây, người dân trên trục đường QL32 cũ đến Cầu Giấy thường xuyên gặp các chuyến tàu của tuyến đường sắt đô thị (metro) Nhổn - ga Hà Nội chạy ngược xuôi trên đoạn trên cao 8,5km từ ga Nhổn đến ga Cầu Giấy và dừng tại các ga.</p><p style=\"text-align:justify;\">Trước đó, từ tháng 7/2021, thi thoảng có các chuyến tàu vận hành với tốc độ chậm hơn.</p><figure class=\"image\"><img src=\"https://cdn.24h.com.vn/upload/4-2021/images/2021-12-14//1639452082-18bacecf2bcfa11e2125cf2db0b10ba1-width700height393.jpg\" alt=\"Metro Nhổn - ga Hà Nội có kịp khai thác cuối 2022? - 1\"></figure><p style=\"text-align:justify;\"><i>Đoàn tàu Nhổn - ga Hà Nội bắt đầu được vận hành ở chế độ tự động với tốc độ 80km/h</i></p><p style=\"text-align:justify;\">Ông Lê Trung Hiếu, Phó trưởng Ban Quản lý đường sắt đô thị Hà Nội (MRB) cho biết, đoàn tàu Nhổn - ga Hà Nội bắt đầu được vận hành ở chế độ tự động với tốc độ 80km/h (tốc độ lớn nhất theo thiết kế) từ ngày 6/12 và kiểm tra, đánh giá chất lượng.</p><p style=\"text-align:justify;\">Sau đó sẽ chuyển sang bước thử nghiệm chạy thử (test run) để kiểm tra, nghiệm thu tổng thể hệ thống thiết bị.</p><p style=\"text-align:justify;\">“Trước khi các đoàn tàu đưa về dự án đã được nhà sản xuất chạy thử 10.000km tại Pháp để căn chỉnh theo các thông số kỹ thuật thiết kế. Khi tàu được đưa về dự án tiếp tục được chạy thử 1.000km nữa. Sau khi đánh giá đạt đủ tiêu chuẩn theo thiết kế mới đưa vào vận hành khai thác. Giai đoạn thử nghiệm chế độ tự động ở tốc độ cao nhất là bước quan trọng để chuẩn bị cho đăng kiểm, kiểm tra nghiệm thu chạy thử và đánh giá độc lập về an toàn vận hành đoàn tàu”, MRB thông tin.</p><p style=\"text-align:justify;\">Liên quan đến việc kiểm định đoàn tàu, Phòng Đường sắt, Cục Đăng kiểm VN cho biết, đã nhận được đề nghị đăng kiểm các đoàn tàu Nhổn - ga Hà Nội.</p><p style=\"text-align:justify;\">Đơn vị này sẽ tiến hành kiểm định ngay khi dự án sẵn sàng các điều kiện hạ tầng kỹ thuật của tuyến trên cao để phục vụ kiểm định phương tiện ở trạng thái tĩnh và vận hành.</p><p style=\"text-align:justify;\">Đại diện MRB cho biết, đến nay dự án không kịp đưa vào khai thác trước 8,5km trên cao vào cuối năm 2021 như dự kiến và phải lùi đến cuối năm 2022.</p><p style=\"text-align:justify;\">“Năm 2020 - 2021 ảnh hưởng dịch Covid-19 gây chậm nhập khẩu thiết bị, huy động chuyên gia và một số khó khăn, vướng mắc kéo dài trước đó khiến dự án chậm tiến độ. Dự án sẽ hoàn thành, khai thác trước đoạn trên cao vào cuối năm 2022 và toàn tuyến vào năm 2025. Mới đây, Bí thư Thành ủy Hà Nội Đinh Tiến Dũng đi kiểm tra dự án, chỉ đạo các đơn vị liên quan quyết tâm thực hiện mục tiêu trên”, Phó trưởng ban MRB Lê Trung Hiếu nói.</p><p style=\"text-align:justify;\">Theo MRB, hiện tuyến trên cao đạt 89,5% tiến độ tổng thể (toàn dự án đạt khoảng 74%), với 2/9 gói thầu xây lắp, thiết bị đã thi công xong (đoạn trên cao và hạ tầng kỹ thuật Depot).</p><p style=\"text-align:justify;\">Hiện một số gói thầu còn 1 - 8% khối lượng công việc, song vẫn có gói thầu khối lượng công việc khá lớn, trong đó gói thầu CP05 (kiến trúc Depot) còn 30% khối lượng, gói CP09 (hệ thống thẻ vé tự động) còn tới 49%.</p>', 'post', NULL, '/data/cms-image/1/tintuc/2022.2.14.17.615.kbc.jpg', NULL, NULL, NULL, NULL, NULL, 'khoa-binh-chung-chuan-bi-bai-giang-cho-lop-tap-huan-cua-su-doan-10-quan-doan-3', NULL, NULL, NULL, NULL, NULL, '2022-11-25T10:31', NULL, NULL, NULL, NULL, '2', NULL, NULL, 'Mẫu-định-dạng-cho-ĐỒ-ÁN-TN21.docx', NULL, NULL, NULL, NULL, NULL, 6, '2022-11-28 03:22:25', '2022-11-28 03:22:25'),
